import "./Menu.scss"

import React from 'react'

export default function Menu({menuOpen, setMenuOpen}) {
    const handleActive = () => setMenuOpen(false)
    return (
        <div className={"menu " + (menuOpen && "active")}>
            <ul>
                <li onClick={handleActive}> <a href="#home">Home</a> </li>
                <li onClick={handleActive}> <a href="#about">About Me</a> </li>
                <li onClick={handleActive}> <a href="#projects">My Projects</a> </li>
                <li onClick={handleActive}> <a href="#contact">Contact Me</a> </li>
                {/* <li href="#home"></li> */}
            </ul>
        </div>
    )
}
